import React, { Component } from "react";

class BookingStructure extends Component {
    constructor(props) {
        super(props);

        this.state = {
        }
    }

    componentDidMount() {
        this.setState({ moviedates: this.props.moviedates })
    }


    render() {
        return (
            <tbody>
                {this.state.moviedates ? (this.state.moviedates.map((moviedate) =>
                    <tr>
                        <td>
                            {moviedate.Movietitle}
                        </td>
                        <td>
                            {moviedate.Salon}
                        </td>
                        <td>
                            {moviedate.Date}
                        </td>
                        <td>
                            {moviedate.TicketsLeft}
                        </td>
                        <td>
                            <form>
                                <input type={"hidden"} name={"date"} value={moviedate.DateID} />
                                <input type={"hidden"} name={"salon"} value={moviedate.SalonID} />
                                <input type={"hidden"} name={"movie"} value={moviedate.MovieID} />
                                <input type={"hidden"} name={"moviedate"} value={moviedate.MoviedateID} />
                                <button>

                                </button>
                            </form>
                        </td>
                    </tr>))
                    :
                    (<tr>
                        <td>
                            &nbsp;
                        </td>
                    </tr>)
                }
            </tbody>
        )
    }

}


export default BookingStructure;
