import React, { Component } from "react";
import Card from "./Card";
import faker from "faker";


class Body extends Component {
    constructor(props) {
        super(props);

        this.state = {
            Movies: [{ id: 1, img: faker.random.image(), title: "Movie 1", blurb: "Some information about the movie here", booked: false, }, { id: 2, img: faker.random.image(), title: "Movie 2", blurb: "Some information about the movie here", booked: false },]
        }
    }

    /* updateMovie = (Movie, theId) => {
        if (Movie.id == theId) {
            Movie.booked = !Movie.booked;
        }
    } */

    updateMovies = (e) => {
        var theId = e.target.id;
        this.state.Movies.foreach((Movie, theId) => {
            if (Movie.id == theId) {
                Movie.booked = !Movie.booked;
            }
        })
    }

    handleSubmit = (e) => {
        e.preventDefault()
    }

    render() {
        console.log(this.state)
        var Content = this.state.Movies.map(
            (Movie) => {
                return (
                    <Card theId={Movie.id} image={Movie.img} title={Movie.title} blurb={Movie.blurb} updateMethod={this.updateMovies} submitMethod={Movie.handleSubmit} />
                )
            }
        );

        return (
            <div>
                {Content}
            </div>
        );
    }
}

export default Body;
