import React, { Component } from "react";
import Card from "./Card";
import axios from "axios";


class Main extends Component {
    constructor(props) {
        super(props);

        this.state = {
            movies: []
        }
    }

    componentDidMount() {
        axios
            .get('http://localhost:1337/movies')
            .then((res) => {
                this.setState({ movies: res.data })
            })
    }


    render() {
        return (
            <div>
                {
                    this.state.movies.map(
                        movie => {
                            <Card data={movie} />
                        }
                    )
                }
            </div>
        )
    }

}

export default Main;
