import React, { Component } from "react";
import axios from "axios";
import { Redirect, withRouter } from "react-router-dom";

class HandleShedule extends Component {
    constructor(props) {
        super(props);

        this.state = {
            monday: new Date(),
            tuesday: new Date(),
            wednesday: new Date(),
            thursday: new Date(),
            friday: new Date(),
            saturday: new Date(),
            sunday: new Date(),
            salon: 1,
            mon17: 1,
            mon19: 1,
            mon22: 1,
            tue17: 1,
            tue19: 1,
            tue22: 1,
            wed17: 1,
            wed19: 1,
            wed22: 1,
            thu17: 1,
            thu19: 1,
            thu22: 1,
            fri17: 1,
            fri19: 1,
            fri22: 1,
            sat17: 1,
            sat19: 1,
            sat22: 1,
            sun17: 1,
            sun19: 1,
            sun22: 1,
        }
    }
    async componentDidMount() {
        const res1 = await axios.get('http://localhost:1337/movies/')
        const res2 = await axios.get('http://localhost:1337/salons/')
        const res3 = await axios.get('http://localhost:1337/dates/')
        this.setState({ movies: res1.data, salons: res2.data, dates: res3.data })
    }

    handleSalon(e) {
        this.setState({ salon: e.target.value })
    }

    handleMon17 = (e) => {
        this.setState({ mon17: e.target.value })
    }

    handleMon19 = (e) => {
        this.setState({ mon19: e.target.value })
    }

    handleMon22 = (e) => {
        this.setState({ mon22: e.target.value })
    }

    handleTue17 = (e) => {
        this.setState({ tue17: e.target.value })
    }

    handleTue19 = (e) => {
        this.setState({ tue19: e.target.value })
    }

    handleTue22 = (e) => {
        this.setState({ tue22: e.target.value })
    }

    handleWed17 = (e) => {
        this.setState({ wed17: e.target.value })
    }

    handleWed19 = (e) => {
        this.setState({ wed19: e.target.value })
    }

    handleWed22 = (e) => {
        this.setState({ wed22: e.target.value })
    }

    handleThu17 = (e) => {
        this.setState({ thu17: e.target.value })
    }

    handleThu19 = (e) => {
        this.setState({ thu19: e.target.value })
    }

    handleThu22 = (e) => {
        this.setState({ thu22: e.target.value })
    }

    handleFri17 = (e) => {
        this.setState({ fri17: e.target.value })
    }

    handleFri19 = (e) => {
        this.setState({ fri19: e.target.value })
    }

    handleFri22 = (e) => {
        this.setState({ fri22: e.target.value })
    }

    handleSat17 = (e) => {
        this.setState({ sat17: e.target.value })
    }

    handleSat19 = (e) => {
        this.setState({ sat19: e.target.value })
    }

    handleSat22 = (e) => {
        this.setState({ sat22: e.target.value })
    }

    handleSun17 = (e) => {
        this.setState({ sun17: e.target.value })
    }

    handleSun19 = (e) => {
        this.setState({ sun19: e.target.value })
    }

    handleSun22 = (e) => {
        this.setState({ sun22: e.target.value })
    }

    onSubmitHandleSaveWeek = (e) => {
        e.preventDefault();
        // get the last date so we can easily add a week
        axios.get('http://localhost:1337/dates/?salon.id=', this.state.salon).then((res) => {
            var lastDate = res.data[res.data.length - 1];
            this.setState({ latestDate: lastDate });
            if (!this.state.latestDate) {
                this.setState({ latestDate: null });
            }

            //calculate the dates of next week
            if (this.state.latestDate) {
                // latestDate should always be a sunday
                this.setState({
                    monday: this.state.latestDate.add(1).day(),
                    tuesday: this.state.latestDate.add(2).day(),
                    wednesday: this.state.latestDate.add(3).day(),
                    thursday: this.state.latestDate.add(4).day(),
                    friday: this.state.latestDate.add(5).day(),
                    saturday: this.state.latestDate.add(6).day(),
                    sunday: this.state.latestDate.add(7).day(),
                })
            } else {
                var today = new Date();
                var date = today.getFullYear() + '-' + today.getMonth() + '-' + today.getDate();
                switch (today.getDay()) {
                    case 0:
                        // Sunday
                        this.setState({
                            monday: this.state.monday.getFullYear() + '-' + this.state.monday.getMonth() + '-' + (this.state.monday.getDate() + 1),
                            tuesday: this.state.tuesday.getFullYear() + '-' + this.state.tuesday.getMonth() + '-' + (this.state.tuesday.getDate() + 2),
                            wednesday: this.state.wednesday.getFullYear() + '-' + this.state.wednesday.getMonth() + '-' + (this.state.wednesday.getDate() + 3),
                            thursday: this.state.thursday.getFullYear() + '-' + this.state.thursday.getMonth() + '-' + (this.state.thursday.getDate() + 4),
                            friday: this.state.friday.getFullYear() + '-' + this.state.friday.getMonth() + '-' + (this.state.friday.getDate() + 5),
                            saturday: this.state.saturday.getFullYear() + '-' + this.state.saturday.getMonth() + '-' + (this.state.saturday.getDate() + 6),
                            sunday: this.state.sunday.getFullYear() + '-' + this.state.sunday.getMonth() + '-' + this.state.sunday.getDate(),
                        })
                        break;
                    case 1:
                        //monday
                        this.setState({
                            monday: this.state.monday.getFullYear() + '-' + this.state.monday.getMonth() + '-' + this.state.monday.getDate(),
                            tuesday: this.state.tuesday.getFullYear() + '-' + this.state.tuesday.getMonth() + '-' + (this.state.tuesday.getDate() + 1),
                            wednesday: this.state.wednesday.getFullYear() + '-' + this.state.wednesday.getMonth() + '-' + (this.state.wednesday.getDate() + 2),
                            thursday: this.state.thursday.getFullYear() + '-' + this.state.thursday.getMonth() + '-' + (this.state.thursday.getDate() + 3),
                            friday: this.state.friday.getFullYear() + '-' + this.state.friday.getMonth() + '-' + (this.state.friday.getDate() + 4),
                            saturday: this.state.saturday.getFullYear() + '-' + this.state.saturday.getMonth() + '-' + (this.state.saturday.getDate() + 5),
                            sunday: this.state.sunday.getFullYear() + '-' + this.state.sunday.getMonth() + '-' + (this.state.sunday.getDate() + 6),
                        })
                        break;
                    case 2:
                        this.setState({
                            monday: this.state.monday.getFullYear() + '-' + this.state.monday.getMonth() + '-' + (this.state.monday.getDate() + 6),
                            tuesday: this.state.tuesday.getFullYear() + '-' + this.state.tuesday.getMonth() + '-' + (this.state.tuesday.getDate() + 7),
                            wednesday: this.state.wednesday.getFullYear() + '-' + this.state.wednesday.getMonth() + '-' + (this.state.wednesday.getDate() + 8),
                            thursday: this.state.thursday.getFullYear() + '-' + this.state.thursday.getMonth() + '-' + (this.state.thursday.getDate() + 9),
                            friday: this.state.friday.getFullYear() + '-' + this.state.friday.getMonth() + '-' + (this.state.friday.getDate() + 10),
                            saturday: this.state.saturday.getFullYear() + '-' + this.state.saturday.getMonth() + '-' + (this.state.saturday.getDate() + 11),
                            sunday: this.state.sunday.getFullYear() + '-' + this.state.sunday.getMonth() + '-' + (this.state.sunday.getDate() + 12),
                        })
                        break;
                    case 3:
                        this.setState({
                            monday: this.state.monday.getFullYear() + '-' + this.state.monday.getMonth() + '-' + (this.state.monday.getDate() + 5),
                            tuesday: this.state.tuesday.getFullYear() + '-' + this.state.tuesdaygetF.getMonth() + '-' + (this.state.tuesday.getDate() + 6),
                            wednesday: this.state.wednesday.getFullYear() + '-' + this.state.wednesdaygetF.getMonth() + '-' + (this.state.wednesday.getDate() + 7),
                            thursday: this.state.thursday.getFullYear() + '-' + this.state.thursdaygetF.getMonth() + '-' + (this.state.thursday.getDate() + 8),
                            friday: this.state.friday.getFullYear() + '-' + this.state.fridaygetF.getMonth() + '-' + (this.state.friday.getDate() + 9),
                            saturday: this.state.saturday.getFullYear() + '-' + this.state.saturdaygetF.getMonth() + '-' + (this.state.saturday.getDate() + 10),
                            sunday: this.state.sunday.getFullYear() + '-' + this.state.sundaygetF.getMonth() + '-' + (this.state.sunday.getDate() + 11),
                        })
                        break;
                    case 4:
                        this.setState({
                            monday: this.state.monday.getFullYear() + '-' + this.state.mondaygetF.getMonth() + '-' + (this.state.monday.getDate() + 4),
                            tuesday: this.state.tuesday.getFullYear() + '-' + this.state.tuesdaygetF.getMonth() + '-' + (this.state.tuesday.getDate() + 5),
                            wednesday: this.state.wednesday.getFullYear() + '-' + this.state.wednesdaygetF.getMonth() + '-' + (this.state.wednesday.getDate() + 6),
                            thursday: this.state.thursday.getFullYear() + '-' + this.state.thursdaygetF.getMonth() + '-' + (this.state.thursday.getDate() + 7),
                            friday: this.state.friday.getFullYear() + '-' + this.state.fridaygetF.getMonth() + '-' + (this.state.friday.getDate() + 8),
                            saturday: this.state.saturday.getFullYear() + '-' + this.state.saturdaygetF.getMonth() + '-' + (this.state.saturday.getDate() + 9),
                            sunday: this.state.sunday.getFullYear() + '-' + this.state.sundaygetF.getMonth() + '-' + (this.state.sunday.getDate() + 10),
                        })
                        break;
                    case 5:
                        this.setState({
                            monday: this.state.monday.getFullYear() + '-' + this.state.mondaygetF.getMonth() + '-' + (this.state.monday.getDate() + 3),
                            tuesday: this.state.tuesday.getFullYear() + '-' + this.state.tuesdaygetF.getMonth() + '-' + (this.state.tuesday.getDate() + 4),
                            wednesday: this.state.wednesday.getFullYear() + '-' + this.state.wednesdaygetF.getMonth() + '-' + (this.state.wednesday.getDate() + 5),
                            thursday: this.state.thursday.getFullYear() + '-' + this.state.thursdaygetF.getMonth() + '-' + (this.state.thursday.getDate() + 6),
                            friday: this.state.friday.getFullYear() + '-' + this.state.fridaygetF.getMonth() + '-' + (this.state.friday.getDate() + 7),
                            saturday: this.state.saturday.getFullYear() + '-' + this.state.saturdaygetF.getMonth() + '-' + (this.state.saturday.getDate() + 8),
                            sunday: this.state.sunday.getFullYear() + '-' + this.state.sundaygetF.getMonth() + '-' + (this.state.sunday.getDate() + 9),
                        })
                        break;
                    case 6:
                        this.setState({
                            monday: this.state.monday.getFullYear() + '-' + this.state.mondaygetF.getMonth() + '-' + (this.state.monday.getDate() + 2),
                            tuesday: this.state.tuesday.getFullYear() + '-' + this.state.tuesdaygetF.getMonth() + '-' + (this.state.tuesday.getDate() + 3),
                            wednesday: this.state.wednesday.getFullYear() + '-' + this.state.wednesdaygetF.getMonth() + '-' + (this.state.wednesday.getDate() + 4),
                            thursday: this.state.thursday.getFullYear() + '-' + this.state.thursdaygetF.getMonth() + '-' + (this.state.thursday.getDate() + 5),
                            friday: this.state.friday.getFullYear() + '-' + this.state.fridaygetF.getMonth() + '-' + (this.state.friday.getDate() + 6),
                            saturday: this.state.saturday.getFullYear() + '-' + this.state.saturdaygetF.getMonth() + '-' + (this.state.saturday.getDate() + 7),
                            sunday: this.state.sunday.getFullYear() + '-' + this.state.sundaygetF.getMonth() + '-' + (this.state.sunday.getDate() + 8),
                        })
                        break;
                    default:
                        console.log("error")
                }
            }

            //test 
            axios.post('http://localhost:1337/dates/', {
                date: this.state.monday,
                time: "17:00:00.000",
                TicketsLeft: this.state.aSalon.seats,
            }).then((rest) => { console.log("yay: " + rest) })
            // Monday
            //17
            axios.get('http://localhost:1337/salons/?id=' + this.state.salon).then((theSalon) => {
                this.setState({ aSalon: theSalon.data[0] })
                console.log(this.state.mon17)
                axios.get('http://localhost:1337/movies/' + this.state.mon17).then((theMovie) => {
                    console.log(this.state.aSalon);
                    this.setState({ theMovie: theMovie.data[0] })
                    axios.post('http://localhost:1337/dates/', {
                        date: this.state.monday,
                        time: "17:00:00.000",
                        TicketsLeft: this.state.aSalon.seats,
                    }).then((result) => console.log(result)).catch(error => console.log(error))
                })
            })
            //19:30:00.000

            axios.get('http://localhost:1337/movies/?id=' + this.state.mon19).then((theMovie) => {
                axios.post('http://localhost:1337/dates/', {
                    date: this.state.monday,
                    time: "19:30:00.000",
                    salons: { salon: this.state.salon },
                    movie: theMovie.data[0],
                    TicketsLeft: this.state.salon.seats,
                }).then((result1) => console.log(result1))
            })
            //22

            axios.get('http://localhost:1337/movies/?id=' + this.state.mon22).then((theMovie) => {
                axios.post('http://localhost:1337/dates/', {
                    date: this.state.monday,
                    time: "22:00:00.000",
                    salon: this.state.salon,
                    movie: theMovie.data[0],
                    TicketsLeft: this.state.salon.seats,
                })
            })
            // Tuesday
            //17

            axios.get('http://localhost:1337/movies/?id=' + this.state.tue17).then((theMovie) => {
                axios.post('http://localhost:1337/dates/', {
                    date: this.state.tuesday,
                    time: "17:00:00.000",
                    salon: this.state.salon,
                    movie: theMovie.data[0],
                    TicketsLeft: this.state.salon.seats,
                })
            })
            //19:30:00.000

            axios.get('http://localhost:1337/movies/?id=' + this.state.tue19).then((theMovie) => {
                axios.post('http://localhost:1337/dates/', {
                    date: this.state.tuesday,
                    time: "19:30:00.000",
                    salon: this.state.salon,
                    movie: theMovie.data[0],
                    TicketsLeft: this.state.salon.seats,
                })
            })
            //22

            axios.get('http://localhost:1337/movies/?id=' + this.state.tue22).then((theMovie) => {
                axios.post('http://localhost:1337/dates/', {
                    date: this.state.tuesday,
                    time: "22:00:00.000",
                    salon: this.state.salon,
                    movie: theMovie.data[0],
                    TicketsLeft: this.state.salon.seats,
                })
            })
            // Wednesday
            //17
            axios.get('http://localhost:1337/movies/?id=' + this.state.wed17).then((theMovie) => {
                axios.post('http://localhost:1337/dates/', {
                    date: this.state.wednesday,
                    time: "17:00:00.000",
                    salon: this.state.salon,
                    movie: theMovie.data[0],
                    TicketsLeft: this.state.salon.seats,
                })
            })
            //19:30:00.000
            axios.get('http://localhost:1337/movies/?id=' + this.state.wed19).then((theMovie) => {
                axios.post('http://localhost:1337/dates/', {
                    date: this.state.wednesday,
                    time: "19:30:00.000",
                    salon: this.state.salon,
                    movie: theMovie.data[0],
                    TicketsLeft: this.state.salon.seats,
                })
            })
            //22
            axios.get('http://localhost:1337/movies/?id=' + this.state.wed22).then((theMovie) => {
                axios.post('http://localhost:1337/dates/', {
                    date: this.state.wednesday,
                    time: "22:00:00.000",
                    salon: this.state.salon,
                    movie: theMovie.data[0],
                    TicketsLeft: this.state.salon.seats,
                })
            })
            // Thursday
            //17
            axios.get('http://localhost:1337/movies/?id=' + this.state.thu17).then((theMovie) => {
                axios.post('http://localhost:1337/dates/', {
                    date: this.state.thursday,
                    time: "17:00:00.000",
