import React, { Component } from "react";
import axios from "axios";
import { Redirect, withRouter } from "react-router-dom";

class HandleShedule extends Component {
    constructor(props) {
        super(props);

        this.state = {
            date: null || JSON.parse(localStorage.getItem('changeShedule')),
        }
    }

    handleSalon(e) {
        this.setState({ salon: e.value })
    }

    async onSubmitHandleSaveDate(e) {

        // get the movie

        var resM = await axios.get('http://localhost:1337/movies/' + e.target.movie.value)

        axios.put('http://localhost:1337/dates/' + this.state.date, {
            movie: resM
        })

        localStorage.removeItem('changeShedule');
        this.props.history.push("/schedules")
    }

    render() {

        if (this.props.aUserGroup != "admin") {
            return (<Redirect to="/" />)
        }

        return (
            <div className={"addFormMain"}>
                <div className={"addFormWrapper"}>
                    <form className={"addForm"} onSubmit={this.onSubmitHandleSaveDate.bind(this)}>
                        <div>
                            Salong
                        </div>
                        <div>
                            {this.state.date.salons[0].salon.name}
                        </div>
                        <div>
                            Datum
                        </div>
                        <div>
                            {this.state.date.date}
                        </div>
                        <div>
                            Tid
                        </div>
                        <div>
                            {this.state.date.time}
                        </div>
                        <div>
                            Film
                        </div>
                        <div>
                            <select name={"movie"} onChange={this.handleMovie}>
                                {this.state.movies.map((movie) =>
                                    <option value={movie.id}>
                                        {movie.title}
                                    </option>
                                )}
                            </select>
                        </div>

                        <button type="submit">Spara</button>
                    </form >
                </div >
            </div >
        )
    }
}

export default HandleShedule;
