import React from "react";
import { Link } from "react-router-dom"


class Card extends Component {

    setIDForMovie = (e) => {
        e.preventDefault()
        this.props.changeMovie(this.props.data.id);
    }

    render() {
        return (

            <div className={"card"} style={{ width: "18rem" }}>
                <div className={"card-wrapper"}>
                    <div className={"image_container"}>
                        <img src={"http://localhost:1337" + props.data.image[0].formats.thumbnail.url} className={"card-img-top"} alt={"Movie"} />
                    </div>
                    <div className={"card-body"}>
                        <h5 className={"card-title"}>{props.data.title}</h5>
                        <p className={"card-text"}>{props.data.description}</p>
                        <div className={"button-div"}>
                            <Link to={"/movie/"}><button className={"card-button"} id={props.data.title} name={props.data.id} onClick={this.setIDForMovie}>Boka</button></Link>
                        </div>
                    </div>
                </div>
            </div >

        )
    }
}

export default Card;
