import React, { Component } from "react";
import axios from "axios";
import MovieInfo from './MovieInfo';
import BookingStructure from '../customercomp/BookingStructure';


const test = undefined;

class Movie extends Component {
    constructor(props) {
        super(props);
        this.state = {
            movie: undefined,
        }
    }

    componentDidMount() {
        var present = [];
        var numberOfTickts = undefined;
        axios
            .get('http://localhost:1337/movies/',
                {
                    params: {
                        id: this.props.match.params.id
                    }
                })
            .then((res) => {
                axios.get('http://localhost:1337/tickets/')
                    .then((test) => {
                        if (test.data.length === 0) {
                            numberOfTickts = 0;
                            this.setState({ movie: res.data[0] });
                            axios
                                .get('http://localhost:1337/dates/')
                                .then((result) => {
                                    this.state.movie.moviedates.map((moviedate) => {
                                        var i = 0;
                                        while (i < result.data.length) {
                                            if (result.data[i].id === moviedate.date) {
                                                var ticketsLeft = result.data[i].salons[0].seats - numberOfTickts;
                                                var item = { 'Movietitle': res.data[0].title, 'MovieID': res.data[0].id, 'Date': result.data[i].date, 'DateID': result.data[i].id, 'Salon': result.data[i].salons[0].salon, 'SalonID': result.data[i].salons[0].id, 'TicketsLeft': ticketsLeft, 'MoviedateID': moviedate.id }
                                                present.push(item);
                                            }
                                            i = i + 1;
                                        }
                                        return (moviedate)
                                    })
                                })

                            this.setState({ moviedates: present });
                        } else {
                            numberOfTickts = 0
                            test.data.map((ticket) => {
                                if (ticket.NOTickets) {
                                    numberOfTickts = numberOfTickts + ticket.NOTickets
                                }
                            })
                            numberOfTickts = 0;
                            this.setState({ movie: res.data[0] });
                            axios
                                .get('http://localhost:1337/dates/')
                                .then((result) => {
                                    this.state.movie.moviedates.map((moviedate) => {
                                        var i = 0;
                                        while (i < result.data.length) {
                                            if (result.data[i].id === moviedate.date) {
                                                var ticketsLeft = result.data[i].salons[0].seats - numberOfTickts;
                                                var item = { 'Movietitle': res.data[0].title, 'MovieID': res.data[0].id, 'Date': result.data[i].date, 'DateID': result.data[i].id, 'Salon': result.data[i].salons[0].salon, 'SalonID': result.data[i].salons[0].id, 'TicketsLeft': ticketsLeft, 'MoviedateID': moviedate.id }
                                                present.push(item);
                                            }
                                            i = i + 1;
                                        }
                                        return (moviedate)
                                    })
                                })

                            this.setState({ moviedates: present });
                        }
                    })
            })
    }

    handleBooking = (e) => {
        e.preventDefault();
        // get local storage
        var cart = [];
        var item = undefined;
        if (localStorage.getItem('cart')) {
            //add to cart
            cart = JSON.parse(localStorage.getItem('cart'));
            item = { 'movieID': e.target.movieID.value, 'movie': e.target.movie.value, 'dateID': e.target.dateID.value, 'date': e.target.date.value, 'salonID': e.target.salonID.value, 'salon': e.target.salon.value, 'amount': e.target.amount.value, 'moviedate': e.target.moviedate.value, }
            cart.push(item);
        } else {
            //construct cart
            item = { 'movieID': e.target.movieID.value, 'movie': e.target.movie.value, 'dateID': e.target.dateID.value, 'date': e.target.date.value, 'salonID': e.target.salonID.value, 'salon': e.target.salon.value, 'amount': e.target.amount.value, 'moviedate': e.target.moviedate.value, }
            cart.push(item);
        }
        // set local storage
        localStorage.setItem('cart', JSON.stringify(cart))
    }


    render() {
        if (localStorage.getItem('ug') == 'Authenticated') {
            test = 'Authenticated';
        }
        return (
            <div>
                {this.state.userGroup ?
                    <div>
                        <div className={""}>
                            {this.state.movie && <MovieInfo movie={this.state.movie} />}
                        </div>
                        <div className={"showings-card"}>
                            <div className={"showings-card-wrapper"}>
                                <h2>Visningar</h2>
                                <table>
                                    <thead>
                                        <tr>
                                            <th>
                                                Salong
                                        </th>
                                            <th>
                                                Datum och tid
                                        </th>
                                            <th>
                                                Platser kvar
                                        </th>
                                            <th>
                                                Antal biljetter
                                        </th>
                                            <th>
                                                Boka
                                        </th>
                                        </tr>
                                    </thead>
                                </table>
                                <form onSubmit={this.handleBooking}>
                                    <table>

                                        <tbody>
                                            <tr>
                                                <td>
                                                    <input type={"hidden"} name={"salon"} value={2} />
                                                    <input type={"hidden"} name={"salonID"} value={2} />
                                            2
                                        </td>
                                                <td>
                                                    <input type={"hidden"} name={"date"} value={"May 1 2020 17:00"} />
                                                    <input type={"hidden"} name={"dateID"} value={4} />
                                            May 1 2020 17:00
                                        </td>
                                                <td>
                                                    25
                                        </td>
                                                <td>
                                                    <input type={"hidden"} name={"movie"} value={"An apple a day keeps the doctor away"} />
                                                    <input type={"hidden"} name={"movieID"} value={2} />
                                                    <input type={"hidden"} name={"moviedate"} value={7} />
                                                    <input type={'number'} name={"amount"} min={1} max={25} defaultValue={1} />
                                                </td>
                                                <td>
                                                    <button name={"button"} type={"submit"}>
                                                        Lägg i varukorg
                                            </button>
                                                </td>
                                            </tr>
                                        </tbody>
                                    </table>
                                </form>

                                <form onSubmit={this.handleBooking}>
                                    <table>

                                        <tbody>
                                            <tr>
                                                <td>
                                                    <input type={"hidden"} name={"salon"} value={2} />
                                                    <input type={"hidden"} name={"salonID"} value={2} />
                                            2
                                        </td>
                                                <td>
                                                    <input type={"hidden"} name={"date"} value={"May 1 2020 17:00"} />
                                                    <input type={"hidden"} name={"dateID"} value={4} />
                                            May 1 2020 17:00
                                        </td>
                                                <td>
                                                    25
                                        </td>
                                                <td>
                                                    <input type={"hidden"} name={"movie"} value={"An apple a day keeps the doctor away"} />
                                                    <input type={"hidden"} name={"movieID"} value={2} />
                                                    <input type={"hidden"} name={"moviedate"} value={7} />
                                                    <input type={'number'} name={"amount"} min={1} max={25} defaultValue={1} />
                                                </td>
                                                <td>
                                                    <button name={"button"} type={"submit"}>
                                                        Lägg i varukorg
                                            </button>
                                                </td>
                                            </tr>
                                        </tbody>
                                    </table>
                                </form>
                            </div>
                        </div>
                    </div>
                    :
                    <div>
                        <div>
                            {this.state.movie && <MovieInfo movie={this.state.movie} />}
                        </div>
                        <div className={"showings-card"}>
                            <div className={"showings-card-wrapper"}>
                                <h2>Visningar</h2>
                                <table>
                                    <thead>
                                        <tr>
                                            <th>
                                                Salong
                                        </th>
                                            <th>
                                                Datum och tid
                                        </th>
                                            <th>
                                                Platser kvar
                                        </th>
                                            <th>
                                                Antal biljetter
                                        </th>
                                            <th>
                                                Boka
                                        </th>
                                        </tr>
                                    </thead>
                                </table>
                                <form onSubmit={this.handleBooking}>
                                    <table>

                                        <tbody>
                                            <tr>
                                                <td>
                                                    <input type={"hidden"} name={"salon"} value={2} />
                                                    <input type={"hidden"} name={"salonID"} value={2} />
                                            2
                                        </td>
                                                <td>
                                                    <input type={"hidden"} name={"date"} value={"May 1 2020 17:00"} />
                                                    <input type={"hidden"} name={"dateID"} value={4} />
                                            May 1 2020 17:00
                                        </td>
                                                <td>
                                                    25
                                        </td>
                                                <td>
                                                    <input type={"hidden"} name={"movie"} value={"An apple a day keeps the doctor away"} />
                                                    <input type={"hidden"} name={"movieID"} value={2} />
                                                    <input type={"hidden"} name={"moviedate"} value={7} />
                                                    <input type={'number'} name={"amount"} min={1} max={25} defaultValue={1} />
                                                </td>
                                                <td>
                                                    <button name={"button"} type={"submit"} disabled>
                                                        Lägg i varukorg
                                            </button>
                                                </td>
                                            </tr>
                                        </tbody>
                                    </table>
                                </form>

                                <form onSubmit={this.handleBooking}>
                                    <table>

                                        <tbody>
                                            <tr>
                                                <td>
                                                    <input type={"hidden"} name={"salon"} value={2} />
                                                    <input type={"hidden"} name={"salonID"} value={2} />
                                            2
                                        </td>
                                                <td>
                                                    <input type={"hidden"} name={"date"} value={"May 1 2020 17:00"} />
                                                    <input type={"hidden"} name={"dateID"} value={4} />
                                            May 1 2020 17:00
                                        </td>
                                                <td>
                                                    25
                                        </td>
                                                <td>
                                                    <input type={"hidden"} name={"movie"} value={"An apple a day keeps the doctor away"} />
                                                    <input type={"hidden"} name={"movieID"} value={2} />
                                                    <input type={"hidden"} name={"moviedate"} value={7} />
                                                    <input type={'number'} name={"amount"} min={1} max={25} defaultValue={1} />
                                                </td>
                                                <td>
                                                    <button name={"button"} type={"submit"} disabled>
                                                        Lägg i varukorg
                                            </button>
                                                </td>
                                            </tr>
                                        </tbody>
                                    </table>
                                </form>
                            </div>
                        </div>
                    </div>
                }
            </div >
        )
    }
}

