import React, { Component } from "react";
import axios from "axios";
import { Redirect } from "react-router-dom";

class HandleMovies extends Component {
    constructor(props) {
        super(props);

        this.state = {
        }
    }

    async getData() {
        const res = await axios.get('http://localhost:1337/movies/')
        this.setState({ movies: res.data })
        console.log(this.state.movies)
    }

    async onSubmitHandle(e) {
        e.preventDefault()
        var theMovie = await axios.get('http://localhost:1337/movies/?id=', e.target.movie.value);
        localStorage.setItem('changeMovie', JSON.stringify(theMovie.data))
        this.props.history.push("/mod/editMovie")
    }

    render() {

        if (this.props.aUserGroup != "admin") {
            return (<Redirect to="/" />)
        }
        return (
            <div className={"shedulesMain"}>
                <div className={"shedulesWrapper"}>
                    <div>
                        <div>
                            Filmer
                        </div>
                        <div>
                            <Link to="/mod/addMovie">
                                <button>Lägg till Film</button>
                            </Link>
                        </div>
                    </div>
                    <table>
                        <thead>
                            <tr>
                                <th>
                                    Bild
                                </th>
                                <th>
                                    Titel
                                </th>
                                <th>
                                    Pris
                                </th>
                                <th>
                                    Ändra
                                </th>
                            </tr>
                        </thead>
                        <tbody>
                            {this.state.movies.map((movie) =>
                                <tr>
                                    <td>
                                        <img src={movie.image[0].formats.thumbnail.url} />
                                    </td>
                                    <td>
                                        {date.time}
                                    </td>
                                    <td>
                                        {date.salons[0].name}
                                    </td>
                                    <td>
                                        {date.movie.title}
                                    </td>
                                    <td>
                                        {date.TicketsLeft}
                                    </td>
                                    <td>
                                        <form className={"addForm"} onSubmit={this.onSubmitHandle.bind(this)}>
                                            <button type="submit" name="date" value={date.id} className={"addFormButton"}>Edit</button>
                                        </form>
                                    </td>
                                </tr>
                            )}
                        </tbody>
                    </table>
                </div>
            </div>
        )
    }
}

export default HandleMovies;
