import React from "react";
import Card from "./Card";


class Body extends Component {
    constructor(props) {
        super(props);

        this.state = {
            1: { id: 1, img: faker.random.image(), title: "Movie 1", blurb: "Some information about the movie here" },
            onebooked: false,
            2: { id: 2, img: faker.random.image(), title: "Movie 2", blurb: "Some information about the movie here" },
            twobooked: false
        }
    }

    updateMovie = (e) => {
        id = e.target.id;
        if (id == 1) {
            this.setState(onebooked.toggle())
        }
    }

    render() {
        return (
            <div>
                {
                    this.state
                }
            </div>
        )
    }
}

export default Body;

