import React, { Component } from "react";
import axios from "axios";

class Movie extends Component {
    constructor(props) {
        super(props);

        this.state = {
            movie: undefined,
            moviedate: [],
        }
    }

    updateState(res) {
        res.map((entry) => {
            console.log(entry);
            if (entry.movie.id == this.props.match.params.id) {
                console.log("here");
                this.moviedate.append(entry);
            }
        }
        )
    }


    componentDidMount() {
        axios
            .get('http://localhost:1337/movies/' + this.props.match.params.id)
            .then((res) => {
                this.setState({ movie: res.data });
                console.log(this.state.movie);
            }
            )
    }


    handleBooking = (e) => {
        e.preventDefault();
        // get local storage
        var cart = [];
        if (localStorage.getItem('cart')) {
            //add to cart
            cart = localStorage.getItem('cart');
            var item = { 'Movie': e.target.movie.value, 'Date': e.target.date.value, 'Time': e.target.time.value, 'Salon': e.target.salon.value, 'Number': e.target.number.value }
            cart.append(item);
        } else {
            //construct cart
            var item = { 'Movie': e.target.movie.value, 'Date': e.target.date.value, 'Time': e.target.time.value, 'Salon': e.target.salon.value, 'Number': e.target.number.value }
            cart.append(item);
        }
        // set local storage
        localStorage.setItem('cart', cart)
    }

    render() {
        return (
            <div> Test { console.log(this.state)} {console.log(this.props.match.params.id)}</div >
        )
    }
}

export default Movie;
