import React, { Component } from 'react';
import firebase from "../components/FirebaseConfig";

class UserProfile extends Component {
    constructor(props) {
        super(props);

        this.state = {
            condition: true,
            passwordChange: false,
            username: "",
            email: "",
            error: "",
        }
    }

    componentDidMount() {
        firebase.auth().onAuthStateChanged(user => this.setState({ username: user.displayName, email: user.email }))
    }

    changeCondition = (e) => {
        e.preventDefault();
        if (this.state.condition) {
            this.setState({ condition: false });
        } else {
            this.setState({ condition: true });
        }
    }

    changePassword = (e) => {
        e.preventDefault();
        if (this.state.passwordChange) {
            this.setState({ passwordChange: false });
        } else {
            this.setState({ passwordChange: true });
        }
    }

    onChangeOldPassword = (e) => {
        e.preventDefault();
        this.setState({ oldPassword: e });
    }
    onChangeNewPassword = (e) => {
        e.preventDefault();
        this.setState({ newPassword: e });
    }
    onChangeNewPasswordRepeat = (e) => {
        e.preventDefault();
        this.setState({ newPasswordRepeat: e });
        if (this.state.newPassword !== this.state.newPasswordRepeat) {
            error = "Lösenorden är inte samma";
        }
    }

    onSubmitChangePassword = (e) => {
        e.preventDefault();

        const oldPassword = e.target.elements.oldPassword.value;
        const newPassword = e.target.elements.newPassword.value;
        const newPasswordRepeat = e.target.elements.newPasswordRepeat.value;

        if (newPassword !== newPasswordRepeat) {
            error = "Det nya lösenordet matchar inte det gamla lösenordet"
        }
    }

    onSubmitEdit(e) {
        e.preventDefault();

        const email = e.target.elements.email.value;
        const password = e.target.elements.password.value;
        firebase.auth()
            .signInWithEmailAndPassword(email, password).then(res => {
                this.props.userCredentrial(res.user.email);
            }).catch((error) => {
                console.log(error)
            })
    }

    render() {

        return (
            <div>
                {!this.state.passwordChange && this.state.condition ?
                    <table>
                        <tr>
                            <td>
                                Namn:
                        </td>
                            <td>
                                {this.state.username}
                            </td>
                        </tr>
                        <tr>
                            <td>
                                Email:
                        </td>
                            <td>
                                {this.state.email}
                            </td>
                        </tr>
                        <tr>
                            <td>
                                <button onClick={this.changePassword}>Ändra lösenord</button>
                            </td>
                            <td>
                                <button onClick={this.changeCondition}>Ändra min information</button>
                            </td>
                        </tr>
                    </table> :
                    !this.state.passwordChange && !this.state.condition ?
                        <table>
                            <tr>
                                <td>
                                    Namn:
                        </td>
                                <td>
                                    <input type="text" name="username" placeholder={this.state.username}></input>
                                </td>
                            </tr>
                            <tr>
                                <td>
                                    Email:
                        </td>
                                <td>
                                    <input type="email" name="email" placeholder={this.state.email}></input>
                                </td>
                            </tr>
                            <tr>
                                <td>
                                    &nbsp;
                        </td>
                                <td>
                                    <button onClick={this.onSubmitEdit}>Spara</button>
                                </td>
                            </tr>
                        </table> :
                        <table>
                            <tr>
                                <td>
                                    Gammalt lösenord:
                        </td>
                                <td>
                                    <input type="password" name="oldPassword" placeholder={this.state.oldpassword}></input>
                                </td>
                            </tr>
                            <tr>
                                <td>
                                    Nytt Lösenord:
                        </td>
                                <td>
                                    <input type="password" name="newPassword" placeholder={this.state.newpassword}></input>
                                </td>
                            </tr>
                            <tr>
                                <td>
                                    Upprepa Lösenord:
                        </td>
                                <td>
                                    <input type="password" name="newPasswordRepeat" placeholder={this.state.newpasswordrepeat}></input>
                                </td>
                            </tr>
                            <tr>
                                <td>
                                    {this.state.error}
                                </td>
                                <td>
                                    <button onClick={this.onSubmitChangePassword}>Spara</button>
                                </td>
                            </tr>
                        </table>
                }
                }
            </div>
        )
    }
}

export default UserProfile;
