import React, { Component } from "react";
import axios from "axios";
import { Redirect } from "react-router-dom";

class HandleMovies extends Component {
    constructor(props) {
        super(props);

        this.state = {
        }
    }

    async getData() {
        const res = await axios.get('http://localhost:1337/movies/')
        this.setState({ movies: res.data })
        console.log(this.state.movies)
    }

    async onSubmitHandle(e) {
        e.preventDefault()
        var theMovie = await axios.get('http://localhost:1337/movies/?id=', e.target.movie.value);
        localStorage.setItem('changeMovie', JSON.stringify(theMovie.data))
        this.props.history.push("/mod/editShedule")
    }

    render() {

        if (this.props.aUserGroup != "admin") {
            return (<Redirect to="/" />)
        }
        return (
            <div className={"addFormMain"}>
                <div className={"addFormWrapper"}>
                    <div className={"addFormContainer"}>
                        <form className={"addForm"} onSubmit={this.onSubmitHandle.bind(this)}>
                            <div>Datum och tid</div>
                            <div className={"addFormInput"}>
                                <input type="datetime-local" name="datetime" onChange={this.eventChangeDateTime.bind(this)}></input>
                            </div>
                            <div className={"addFormButtonDiv"}>
                                <button type="submit" className={"addFormButton"}>Save</button>
                            </div>
                        </form>
                    </div>
                </div>
            </div >
        )
    }
}

export default HandleMovies;
