import React, { Component } from "react";
import Card from "./Card";
import Apicontact from "./Apicontact";
import axios from "axios";


class Main extends Component {
    constructor(props) {
        super(props);

        this.state = {
            movies: []
        }
    }

    componentDidMount() {
        axios
            .get('http://localhost:1337/movies')
            .then((res) => {
                this.setState({ movies: res.data })
            })
    }


    render() {
        var Content = this.state.Movies.map(
            (Movie) => {
                return (
                    <div>
                        {}
                        <Apicontact />
                        <Card key={Movie.key} theId={Movie.key} image={Movie.img} title={Movie.title} blurb={Movie.blurb} submitMethod={this.handleSubmit} />
                    </div>
                )
            }
        );

        return (
            <main>
                {Content}
            </main>
        );
    }
}

export default Main;
