import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";


class CustomerNav extends Component {

    constructor(props) {
        super(props);

        var itemsFromLocalstorage = JSON.parse(localStorage.getItem('cart'));

        this.state = {
            cart_closed: true,
            cart: itemsFromLocalstorage,
        }
    }

    handleOnclickCart = (e) => {
        e.preventDefault();
        this.state.cart_closed ? this.setState({ cart_closed: false, }) : this.setState({ cart_closed: true, });
    }

    handleSubmitCart = (e) => {
        e.preventDefault();
        // handle booking
        //get user
        axios.get("http:localhost:1337/auth/local", this.props.aUser).then(res => console.log(res))
