                })
        }) */
        // clear cart
        //localStorage.clear();
        window.location.reload(false);
    }

    render() {
        return (

            <nav className={"second-menu"} >
                <ul>
                    <li key={"bookings"}>
                        <Link to="/bookings">
                            <button className={""}>Bokningar</button>
                        </Link>
                    </li>
                    {this.state.cart_closed ?
                        <li key={"closed-cart"}>
                            <form onSubmit={this.handleOnclickCart}>
                                <button className={""} onClick={this.handleOnclickCart} >Kundvagn</button>
                            </form>
                        </li>
                        :
                        <li key={"open-cart"}>
                            <form onSubmit={this.handleOnclickLogin}>
                                <button className={""} onClick={this.handleOnclickLogin} >Kundvagn</button>
                            </form>
                            <ul>
                                <li key={"cart-content"}>
                                    <table>
                                        <thead>
                                            <tr>
                                                <th>
                                                    Film
                                                </th>
                                                <th>
                                                    Datum och Tid
                                                </th>
                                                <th>
                                                    Salong
                                                </th>
                                                <th>
                                                    Biljetter
                                                </th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {this.state.cart.map((item) =>
                                                <tr>
                                                    <td>
                                                        {item.movie}
                                                    </td>
                                                    <td>
                                                        {item.date}
                                                    </td>
                                                    <td>
                                                        {item.salon}
                                                    </td>
                                                    <td>
                                                        {item.amount}
                                                    </td>
                                                </tr>
                                            )}
                                        </tbody>
                                    </table>
                                </li>
                                <li key={"cart-submit"}>
                                    <form onSubmit={this.handleSubmitCart}>
                                        <button type={"submit"} onClick={this.handleSubmitCart}>Boka</button>
                                    </form>
                                </li>
                            </ul>
                        </li>
                    }

                </ul>
            </nav >

        )
    }
}

export default CustomerNav;
