import React, { Component } from "react";
import axios from "axios";
import MovieInfo from './MovieInfo';
import BookingStructure from './BookingStructure';

class Movie extends Component {
    constructor(props) {
        super(props);
        this.state = {
            movie: undefined,
            moviedates: undefined,
        }
    }

    componentDidMount() {
        var presentation = [];
        var numberOfTickts = undefined;
        axios
            .get('http://localhost:1337/movies/',
                {
                    params: {
                        id: this.props.match.params.id
                    }
                })
            .then((res) => {
                axios.get('http://localhost:1337/tickets/')
                    .then((test) => {
                        if (test.data.length == 0) {
                            numberOfTickts = 0;
                            this.setState({ movie: res.data[0] });
                            axios
                                .get('http://localhost:1337/dates/')
                                .then((result) => {
                                    this.state.movie.moviedates.map((moviedate) => {
                                        var i = 0;
                                        while (i < result.data.length) {
                                            if (result.data[i].id == moviedate.date) {
                                                console.log(result.data[i]);
                                                var ticketsLeft = result.data[i].salons[0].seats - numberOfTickts;
                                                var item = { 'Movietitle': res.data[0].title, 'MovieID': res.data[0].id, 'Date': result.data[i].date, 'DateID': result.data[i].id, 'Salon': result.data[i].salons[0].salon, 'SalonID': result.data[i].salons[0].id, 'TicketsLeft': ticketsLeft }
                                                presentation.push(item)
                                            }
                                            i = i + 1;
                                        }
                                    });
                                    this.setState({ moviedates: presentation });
                                })
                        } else {
                            console.log(test)
                        }
                    })
            })
    }

    render() {
        return (
            <div>
                {this.state.movie ? <MovieInfo movie={this.state.movie} /> : <div></div>}
                {this.state.moviedates ? < BookingStructure moviedates={this.state.moviedates} /> : <div></div>}

            </div >
        )
    }
}

export default Movie;
