import React, { Component } from "react";

class BookingStructure extends Component {
    constructor(props) {
        super(props);

        this.state = {
        }
    }

    componentDidMount() {
        this.setState({ moviedates: this.props.moviedates })
    }

    handleBooking = (e) => {
        e.preventDefault();
        // get local storage
        var cart = [];
        var item = undefined;
        if (localStorage.getItem('cart')) {
            //add to cart
            cart = localStorage.getItem('cart');
            item = { 'Movie': e.target.movie.value, 'Date': e.target.date.value, 'Time': e.target.time.value, 'Salon': e.target.salon.value, 'Number': e.target.number.value }
            cart.append(item);
        } else {
            //construct cart
            item = { 'Movie': e.target.movie.value, 'Date': e.target.date.value, 'Time': e.target.time.value, 'Salon': e.target.salon.value, 'Number': e.target.number.value }
            cart.append(item);
        }
        // set local storage
        localStorage.setItem('cart', cart)
    }


    render() {
        return (
            <div className={"card"} style={{ width: "18rem" }}>
                <div className={"card-wrapper"}>
                    {this.state.moviedates && this.state.moviedates.map((moviedate) =>
                        <form onSubmit={this.handleBooking.bind(this)}>
                            <table>
                                <tbody>
                                    <tr>
                                        <td>
                                            <input type={"hidden"} name={"movie"} value={moviedate.MovieID} />
                                            {moviedate.Movietitle}
                                        </td>
                                        <td>
                                            <input type={"hidden"} name={"salon"} value={moviedate.SalonID} />
                                            {moviedate.Salon}
                                        </td>
                                        <td>
                                            <input type={"hidden"} name={"date"} value={moviedate.DateID} />
                                            {moviedate.Date}
                                        </td>
                                        <td>
                                            {moviedate.TicketsLeft}
                                        </td>
                                        <td>
                                            <input type={"hidden"} name={"moviedate"} value={moviedate.MoviedateID} />
                                            <input type={'number'} name={"amount"} max={moviedate.TicketsLeft} />
                                        </td>
                                        <td>
                                            <button onClick={this.handleBooking.bind(this)}>
                                                Lägg i varukorg
                                    </button>
                                        </td>
                                    </tr>
                                </tbody>
                            </table>
                        </form>
                    )}
                </div>
            </div>
        )
    }

}


export default BookingStructure;
