import React, { Component } from "react";
import axios from "axios";

class addMovie extends Component {
    constructor(props) {
        super(props);

        this.state = {
            title: "",
            blurb: "",
            price: "",
            imageFile: "",
        }
    }

    async eventChangeTitle(e) {
        this.setState({ title: e.target.value })
        console.log(this.state)
    }

    async eventChangeBlurb(e) {
        this.setState({ blurb: e.target.value })
        console.log(this.state)
    }

    async eventChangePrice(e) {
        this.setState({ price: e.target.value })
        console.log(this.state)
    }

    async eventChangeImage(e) {
        this.setState({ imageFile: e.target.files[0].name })
        console.log(this.state)
        console.log(e.target.files[0].name)
    }

    async onSubmitHandle(e) {
        e.preventDefault()
        // add data to movies
        axios.get('http://localhost:1337/upload/files/;id')

        const res = await axios.post('http://localhost:1337/movies/', {
            title: this.state.title,
            description: this.state.blurb,
            price: this.state.price,
            image: "http://localhost:1337/upload/" + this.state.imageFile
        })
        console.log(res)
    }

    render() {
        return (
            <form onSubmit={this.onSubmitHandle.bind(this)}>
                <input type="text" name="title" onChange={this.eventChangeTitle.bind(this)}></input>
                <input type="text" name="blurb" onChange={this.eventChangeBlurb.bind(this)}></input>
                <input type="number" name="price" onChange={this.eventChangePrice.bind(this)}></input>
                <input type="file" name="imageFile" onChange={this.eventChangeImage.bind(this)}></input>
                <button type="submit">Save</button>
            </form>
        )
    }
}

export default addMovie;
