import React, { Component } from "react";


class Button extends Component {
    constructor(props) {
        super(props);

        this.state = { Movies: [{ id: 1, img: faker.random.image(), title: "Movie 1", blurb: "Some information about the movie here" }, { id: 2, img: faker.random.image(), title: "Movie 2", blurb: "Some information about the movie here" }] }
    }
}

/* const Button = (props) => {
    var theClass = "button";
    if (props.buttonType == "card") {
        theClass = "card-button";
    } else if (props.buttonType == "nav") {
        theClass = "nav-button";
    }
    return (

        <button className={theClass} onClick={props.updateMethod(props.id, "Movie 3", props.blurb)} > {props.buttonName}}
        </button >

    )
} */

export default Button;
