import React, { Component } from "react";
import axios from "axios";

class addMovie extends Component {
    constructor(props) {
        super(props);

        this.state = {
            title: "",
            blurb: "",
            price: "",
            imageFile: "",
        }
    }

    async onSubmitHandle(e) {
        e.preventDefault()
        // image first so we have an url
        /* var file = e.target.elemets.imageFile.files[0];
        axios
            .post('http://localhost:1337/uploads/', {

            })
            .then((rest) => {
                console.log(res)
            }) */
        this.setState({ title: e.target.elements.title.value, blurb: e.target.elements.blurb.value, price: e.target.elements.price.value, imageFile: e.target.elements.imageFile.files[0].name })
        console.log(this.state)
        // add data to movies
        const res = await axios.post('http://localhost:1337/movies/', {
            title: e.target.elements.title.value,
            blurb: e.target.elements.blurb.value,
            price: e.targets.elements.price.value,
            image: "http://localhost:1337/uploads/" + e.target.elements.imageFile.files[0].name
        })
    }

    render() {
        return (
            <form onSubmit={this.onSubmitHandle.bind(this)}>
                <input type="text" name="title"></input>
                <input type="text" name="blurb"></input>
                <input type="number" name="price"></input>
                <input type="file" name="imageFile"></input>
                <button type="submit">Save</button>
            </form>
        )
    }
}

export default addMovie;
