import React, { Component } from "react";
import axios from "axios";

class BookingStructure extends Component {
    constructor(props) {
        super(props);

        this.state = {
            salons: null,
            moviedates: undefined,
        }
    }

    componentDidMount() {
        axios
            .get('http://localhost:1337/salons/')
            .then((res) => {
                var presentation = []
                var movieDates = this.props.moviedates;
                movieDates.map((moviedate) => {
                    var item = { 'Movie': moviedate.movie.title, 'Date': moviedate.date.date, "IDm": moviedate.id, "IDd": moviedate.date.id }
                    axios
                        .get('http://localhost:1337/dates/' + item.IDd)
                        .then((result) => {
                            item.salon = result.data.salons.salon;
                            item.seats = result.data.salons.seats;
                            axios
                                .get('http://localhost:1337/tickets/')
                                .then((tres) => {
                                    if (tres.data.length > 0) {
                                        item.seatsLeft = item.seats - tres.data.NOTickets
                                    } else {
                                        item.seatsLeft = item.seats
                                    }
                                    presentation.push(item)
                                })
                        })
                })
            })
    }

    handleBooking = (e) => {
        e.preventDefault();
        // get local storage
        var cart = [];
        var item = undefined;
        if (localStorage.getItem('cart')) {
            //add to cart
            cart = localStorage.getItem('cart');
            item = { 'Movie': e.target.movie.value, 'Date': e.target.date.value, 'Time': e.target.time.value, 'Salon': e.target.salon.value, 'Number': e.target.number.value }
            cart.append(item);
        } else {
            //construct cart
            item = { 'Movie': e.target.movie.value, 'Date': e.target.date.value, 'Time': e.target.time.value, 'Salon': e.target.salon.value, 'Number': e.target.number.value }
            cart.append(item);
        }
        // set local storage
        localStorage.setItem('cart', cart)
    }

    render() {
        if (!this.state.salons) {
            return (

                <div className={"card"} style={{ width: "18rem" }}>
                    <div></div>
                </div >

            )
        } else {
            console.log(this.state.moviedates)
            return (

                <div className={"card"} style={{ width: "18rem" }}>
                    <div className={"card-wrapper"}>
                        <table>
                            <tbody>
                                {this.state.moviedates.map((moviedate) =>
                                    <tr>
                                        <td>
                                            {moviedate.movie.title}
                                        </td>
                                        <td>
                                            {moviedate.date.salon.salon}
                                        </td>
                                        <td>
                                            {moviedate.movie.title}
                                        </td>
                                        <td>
                                            <form onSubmit={this.handleBooking}>
                                                <button onClick={this.handleBooking} value={this.moviedate}>
                                                    Boka
                                                </button>
                                            </form>
                                        </td>
                                    </tr>
                                )}
                            </tbody>
                        </table>
                    </div>
                </div >

            )
        }
    }
}

export default BookingStructure;
