import React, { Component } from "react";
import axios from "axios";

class Movie extends Component {
    constructor(props) {
        super(props);

        this.state = {
            moviedate: [],
        }
    }

    componentDidMount() {
        var movieDates = []
        var movie_dates = []
        axios
            .get('http://localhost:1337/moviedates/id?' + this.props.match.params.id)
            .then((res) => {
                movie_dates = res.data
            })
        movie_dates.map((entry) => {
            console.log(entry)
            if (entry.movie.id == this.props.match.params.id) {
                console.log("here")
                movieDates.append(entry)
            }
        })
        this.setState({ moviedate: movieDates })
    }

    handleBooking = (e) => {
        e.preventDefault();
        // get local storage
        var cart = [];
        if (localStorage.getItem('cart')) {
            //add to cart
            cart = localStorage.getItem('cart');
            var item = { 'Movie': e.target.movie.value, 'Date': e.target.date.value, 'Time': e.target.time.value, 'Salon': e.target.salon.value, 'Number': e.target.number.value }
            cart.append(item);
        } else {
            //construct cart
            var item = { 'Movie': e.target.movie.value, 'Date': e.target.date.value, 'Time': e.target.time.value, 'Salon': e.target.salon.value, 'Number': e.target.number.value }
            cart.append(item);
        }
        // set local storage
        localStorage.setItem('cart', cart)
    }

    render() {
        return (
            <div> Test { console.log(this.state)} {console.log(this.props.match.params.id)}</div>
        )
    }
}

export default Movie;
