import React, { Component } from "react";
import axios from "axios";

class Movie extends Component {
    constructor(props) {
        super(props);

        this.state = {
            movie: [],
            moviedate: [],
            date: [],
        }
    }

    componentDidMount() {
        axios
            .get('http://localhost:1337/movies/' + this.props.match.params.id)
            .then((res) => {
                return (
                    this.setState({ movie: res.data })
                )
            })
        movieDates = []
        movie_dates = []
        axios
            .get('http://localhost:1337/moviedates/')
            .then((res) => {
                return (
                    movie_dates = res.data
                )
            })
        movie_dates.foreach((entry) => {
            if (entry.movie.id != this.props.match.params.id) {
                movieDates.append(entry)
            }
        })
        this.setState({ moviedate: movieDates })
    }

    render() {
        return (
            <div> Test { console.log(this.state)}</div>
        )
    }
}

export default Movie;
