import React, { Component } from "react";
import axios from "axios";
import MovieInfo from './MovieInfo';
import BookingStructure from './BookingStructure';

class Movie extends Component {
    constructor(props) {
        super(props);
        this.getData();
        this.state = {
            movie: undefined,
            moviedates: undefined,
        }
    }

    getData() {
        axios
            .get('http://localhost:1337/movies/' + this.props.match.params.id)
            .then((res) => {
                this.setState({ movie: res.data });
                this.state.movie.moviedates.map((moviedate) => {
                    axios
                        .get('http://localhost:1337/moviedates/' + moviedate.id)
                        .then((res) => {
                            if (this.state.moviedate) {
                                var movieDate = this.state.moviedate;
                                movieDate.push(res.data)
                                this.setState({ moviedates: movieDate });
                            } else {
                                var movieDate = [];
                                movieDate.push(res.data)
                                this.setState({ moviedates: movieDate });
                            }
                        })
                })
            })
    }

    componentDidMount() {
        axios
            .get('http://localhost:1337/movies/' + this.props.match.params.id)
            .then((res) => {
                this.setState({ movie: res.data });
                this.state.movie.moviedates.map((moviedate) => {
                    axios
                        .get('http://localhost:1337/moviedates/' + moviedate.id)
                        .then((res) => {
                            if (this.state.moviedate) {
                                var movieDate = this.state.moviedate;
                                movieDate.push(res.data)
                                this.setState({ moviedates: movieDate });
                            } else {
                                var movieDate = [];
                                movieDate.push(res.data)
                                this.setState({ moviedates: movieDate });
                            }
                        })
                })
            })
    }


    handleBooking = (e) => {
        e.preventDefault();
        // get local storage
        var cart = [];
        var item = undefined;
        if (localStorage.getItem('cart')) {
            //add to cart
            cart = localStorage.getItem('cart');
            item = { 'Movie': e.target.movie.value, 'Date': e.target.date.value, 'Time': e.target.time.value, 'Salon': e.target.salon.value, 'Number': e.target.number.value }
            cart.append(item);
        } else {
            //construct cart
            item = { 'Movie': e.target.movie.value, 'Date': e.target.date.value, 'Time': e.target.time.value, 'Salon': e.target.salon.value, 'Number': e.target.number.value }
            cart.append(item);
        }
        // set local storage
        localStorage.setItem('cart', cart)
    }

    render() {
        return (
            <div>
                <MovieInfo movie={this.state.movie} />
                <BookingStructure moviedates={this.state.moviedate} />
            </div >
        )
    }
}

export default Movie;
