import React, { Component } from "react";
import Card from "./Card";
import faker from "faker";


class Body extends Component {
    constructor(props) {
        super(props);

        this.state = {
            Movies: [{ id: 1, img: faker.random.image(), title: "Movie 1", blurb: "Some information about the movie here", onebooked: false, }, { id: 2, img: faker.random.image(), title: "Movie 2", blurb: "Some information about the movie here", twobooked: false },]
        }
    }

    updateMovie(Movie, theId) {
        if (Movie.id == theId) {
            Movie.booked = !Movie.booked;
        }
    }

    updateMovies = (e) => {
        var theId = e.target.id;
        this.state.Movies.foreach(updateMovie(theId))
    }

    render() {
        console.log(this.state)
        var Content = this.state.Movies.map(
            (movie) => {
                return (
                    <Card theId={movie.id} image={movie.image} title={movie.title} blurb={movie.blurb} updateMethod={movie.updateMovies} />
                )
            }
        );

        return (
            <div>
                {Content}
            </div>
        );
    }
}

export default Body;
