import React, { Component } from "react";
import axios from "axios";
import MovieInfo from './MovieInfo';
import BookingStructure from './BookingStructure';

class Movie extends Component {
    constructor(props) {
        super(props);
        this.state = {
            movie: undefined,
            moviedates: undefined,
        }
    }

    componentDidMount() {
        axios
            .get('http://localhost:1337/movies/',
                {
                    params: {
                        id: this.props.match.params.id
                    }
                })
            .then((res) => {
                axios.get('http://localhost:1337/tickets/')
                    .then((test) => {
                        if (test.data.length == 0) {
                            this.setState({ movie: res.data[0] });
                            var presentation = [];
                            var numberOfTickts = undefined;
                            axios
                                .get('http://localhost:1337/tickets/')
                                .then((tres) => {
                                    if (tres.data.length == 0) {
                                        numberOfTickts = 0;
                                        this.state.movie.moviedates.map((moviedate) => {
                                            axios
                                                .get('http://localhost:1337/dates/',
                                                    {
                                                        params: {
                                                            id: moviedate.date
                                                        }
                                                    })
                                                .then((result) => {
                                                    if (this.state.moviedates) {


                                                    } else {
                                                        console.log(result)
                                                        var ticketsLeft = result.data[0].salons[0].seats - numberOfTickts;
                                                        var item = { 'Movietitle': res.data[0].title, 'MovieID': res.data[0].id, 'Date': result.data[0].date, 'DateID': result.data[0].id, 'Salon': result.data[0].salons[0].salon, 'SalonID': result.data[0].salons[0].id, 'TicketsLeft': ticketsLeft }
                                                        console.log(item)
                                                        presentation.push(item)
                                                    }
                                                })
                                        })
                                        this.setState({ moviedates: presentation })
                                        console.log(this.state.moviedates)
                                    } else {
                                        console.log(tres)
                                    }
                                })
                        }
                    })
            })
    }

    render() {
        return (
            <div>
                {this.state.movie ? <MovieInfo movie={this.state.movie} /> : <div></div>}
                {this.state.moviedates ? < BookingStructure moviedates={this.state.moviedates} /> : <div></div>}

            </div >
        )
    }
}

export default Movie;
