import React, { Component } from "react";
import axios from "axios";
import { Redirect, withRouter } from "react-router-dom";

class HandleShedule extends Component {
    constructor(props) {
        super(props);

        this.state = {
            salon: 1,
        }
    }
    async componentDidMount() {
        const res1 = await axios.get('http://localhost:1337/movies/')
        const res2 = await axios.get('http://localhost:1337/salons/')
        const res3 = await axios.get('http://localhost:1337/dates/')
        this.setState({ movies: res1.data, salons: res2.data, dates: res3.data })
    }

    handleSalon(e) {
        this.setState({ salon: e.target.value })
    }

    onSubmitHandleSaveWeek(e) {
        e.preventDefault();
        // get the last date so we can easily add a week
        axios.get('http://localhost:1337/dates/?salons.id=', this.state.salon).then((res) => {
            var lastDate = res.data[res.data.length - 1];
            this.setState({ latestDate: lastDate });
            if (!this.state.latestDate) {
                this.setState({ latestDate: null });
            }

            //calculate the dates of next week
            var monday = new Date();
            var tuesday = new Date();
            var wednesday = new Date();
            var thursday = new Date();
            var friday = new Date();
            var saturday = new Date();
            var sunday = new Date();
            if (this.state.latestDate) {
                // latestDate should always be a sunday
                monday = this.state.latestDate.add(1).day()
                tuesday = this.state.latestDate.add(2).day();
                wednesday = this.state.latestDate.add(3).day();
                thursday = this.state.latestDate.add(4).day();
                friday = this.state.latestDate.add(5).day();
                saturday = this.state.latestDate.add(6).day();
                sunday = this.state.latestDate.add(7).day();
            } else {
                var today = new Date();
                var date = today.getFullYear() + '-' + today.getMonth() + '-' + today.getDate();
                switch (today.getDay()) {
                    case 0:
                        // Sunday
                        monday = monday.getFullYear() + '-' + monday.getMonth() + '-' + (monday.getDate() + 1);
                        today.add(1).day();
                        tuesday = tuesday.getFullYear() + '-' + tuesday.getMonth() + '-' + (tuesday.getDate() + 2);
                        wednesday = wednesday.getFullYear() + '-' + wednesday.getMonth() + '-' + (wednesday.getDate() + 3);
                        thursday = thursday.getFullYear() + '-' + thursday.getMonth() + '-' + (thursday.getDate() + 4);
                        friday = friday.getFullYear() + '-' + friday.getMonth() + '-' + (friday.getDate() + 5);
                        saturday = saturday.getFullYear() + '-' + saturday.getMonth() + '-' + (saturday.getDate() + 6);
                        sunday = sunday.getFullYear() + '-' + sunday.getMonth() + '-' + sunday.getDate();
                        break;
                    case 1:
                        //monday
                        monday = monday.getFullYear() + '-' + monday.getMonth() + '-' + monday.getDate();
                        tuesday = tuesday.getFullYear() + '-' + tuesday.getMonth() + '-' + (tuesday.getDate() + 1);
                        wednesday = wednesday.getFullYear() + '-' + wednesday.getMonth() + '-' + (wednesday.getDate() + 2);
                        thursday = thursday.getFullYear() + '-' + thursday.getMonth() + '-' + (thursday.getDate() + 3);
                        friday = friday.getFullYear() + '-' + friday.getMonth() + '-' + (friday.getDate() + 4);
                        saturday = saturday.getFullYear() + '-' + saturday.getMonth() + '-' + (saturday.getDate() + 5);
                        sunday = sunday.getFullYear() + '-' + sunday.getMonth() + '-' + (sunday.getDate() + 6);
                        break;
                    case 2:
                        monday = monday.getFullYear() + '-' + monday.getMonth() + '-' + (monday.getDate() + 6);
                        tuesday = tuesday.getFullYear() + '-' + tuesday.getMonth() + '-' + (tuesday.getDate() + 7);
                        wednesday = wednesday.getFullYear() + '-' + wednesday.getMonth() + '-' + (wednesday.getDate() + 8);
                        thursday = thursday.getFullYear() + '-' + thursday.getMonth() + '-' + (thursday.getDate() + 9);
                        friday = friday.getFullYear() + '-' + friday.getMonth() + '-' + (friday.getDate() + 10);
                        saturday = saturday.getFullYear() + '-' + saturday.getMonth() + '-' + (saturday.getDate() + 11);
                        sunday = sunday.getFullYear() + '-' + sunday.getMonth() + '-' + (sunday.getDate() + 12);
                        break;
                    case 3:
                        monday = monday.getFullYear() + '-' + monday.getMonth() + '-' + (monday.getDate() + 5);
                        tuesday = tuesday.getFullYear() + '-' + tuesday.getMonth() + '-' + (tuesday.getDate() + 6);
                        wednesday = wednesday.getFullYear() + '-' + wednesday.getMonth() + '-' + (wednesday.getDate() + 7);
                        thursday = thursday.getFullYear() + '-' + thursday.getMonth() + '-' + (thursday.getDate() + 8);
                        friday = friday.getFullYear() + '-' + friday.getMonth() + '-' + (friday.getDate() + 9);
                        saturday = saturday.getFullYear() + '-' + saturday.getMonth() + '-' + (saturday.getDate() + 10);
                        sunday = sunday.getFullYear() + '-' + sunday.getMonth() + '-' + (sunday.getDate() + 11);
                        break;
                    case 4:
