                        saturday = saturday.getFullYear() + '-' + saturday.getMonth() + '-' + (saturday.getDate() + 9);
                        sunday = sunday.getFullYear() + '-' + sunday.getMonth() + '-' + (sunday.getDate() + 10);
                        break;
                    case 5:
                        monday = monday.getFullYear() + '-' + monday.getMonth() + '-' + (monday.getDate() + 3);
                        tuesday = tuesday.getFullYear() + '-' + tuesday.getMonth() + '-' + (tuesday.getDate() + 4);
                        wednesday = wednesday.getFullYear() + '-' + wednesday.getMonth() + '-' + (wednesday.getDate() + 5);
