                        saturday = saturday.getFullYear() + '-' + saturday.getMonth() + '-' + (saturday.getDate() + 8);
                        sunday = sunday.getFullYear() + '-' + sunday.getMonth() + '-' + (sunday.getDate() + 9);
                        break;
                    case 6:
                        monday = monday.getFullYear() + '-' + monday.getMonth() + '-' + (monday.getDate() + 2);
                        tuesday = tuesday.getFullYear() + '-' + tuesday.getMonth() + '-' + (tuesday.getDate() + 3);
                        wednesday = wednesday.getFullYear() + '-' + wednesday.getMonth() + '-' + (wednesday.getDate() + 4);
                        thursday = thursday.getFullYear() + '-' + thursday.getMonth() + '-' + (thursday.getDate() + 5);
                        friday = friday.getFullYear() + '-' + friday.getMonth() + '-' + (friday.getDate() + 6);
                        saturday = saturday.getFullYear() + '-' + saturday.getMonth() + '-' + (saturday.getDate() + 7);
                        sunday = sunday.getFullYear() + '-' + sunday.getMonth() + '-' + (sunday.getDate() + 8);
                        break;
                    default:
                        console.log("error")
                }
