import React, { Component } from "react";
import axios from "axios";

class BookingStructure extends Component {
    constructor(props) {
        super(props);

        this.state = {
            salons: undefined,
            moviedates: undefined,
        }
    }

    componentDidMount() {
        console.log(this.props)
        axios
            .get('http://localhost:1337/salons/')
            .then((res) => {
                this.setState({ salons: res.data });
                console.log(res);
                console.log(res.data);
                this.setState({ moviedates: this.props.moviedates });
            })
    }

    handleBooking = (e) => {
        e.preventDefault();
        // get local storage
        var cart = [];
        var item = undefined;
        if (localStorage.getItem('cart')) {
            //add to cart
            cart = localStorage.getItem('cart');
            item = { 'Movie': e.target.movie.value, 'Date': e.target.date.value, 'Time': e.target.time.value, 'Salon': e.target.salon.value, 'Number': e.target.number.value }
            cart.append(item);
        } else {
            //construct cart
            item = { 'Movie': e.target.movie.value, 'Date': e.target.date.value, 'Time': e.target.time.value, 'Salon': e.target.salon.value, 'Number': e.target.number.value }
            cart.append(item);
        }
        // set local storage
        localStorage.setItem('cart', cart)
    }

    render() {
        console.log(this.state.salons);
        return (

            <div className={"card"} style={{ width: "18rem" }}>

                {this.state.salons ? <div></div> :
                    <div className={"card-wrapper"}>
                        <table>
                            <thead>
                                <tr>
                                    <th>
                                        &nbsp;
                                </th>
                                    {this.state.salons.map((salon) =>
                                        <th>
                                            {salon.salon}
                                        </th>
                                    )}
                                </tr>
                            </thead>
                            <tbody>
                                <tr>
                                    <td>

                                    </td>
                                </tr>
                            </tbody>
                        </table>
                    </div>
                }
            </div >

        )
    }
}

export default BookingStructure;
