import React, { Component } from "react";
import axios from "axios";
import MovieInfo from './MovieInfo';
import BookingStructure from './BookingStructure';

class Movie extends Component {
    constructor(props) {
        super(props);
        this.state = {
            movie: undefined,
            moviedates: null,
        }
    }

    componentDidMount() {
        axios
            .get('http://localhost:1337/movies/' + this.props.match.params.id)
            .then((res) => {
                this.setState({ movie: res.data });
                this.state.movie.moviedates.map((moviedate) => {
                    axios
                        .get('http://localhost:1337/moviedates/' + moviedate.id)
                        .then((res) => {
                            if (this.state.moviedate != undefined) {
                                var movieDate = this.state.moviedate;
                                movieDate.push(res.data)
                                this.setState({ moviedates: movieDate });
                                console.log(this.state.moviedates)
                            } else {
                                var movieDate = [];
                                movieDate.push(res.data)
                                this.setState({ moviedates: movieDate });
                                console.log(this.state.moviedates)
                            }
                        })
                })
            })
    }

    render() {
        return (
            <div>
                {this.state.movie ? <MovieInfo movie={this.state.movie} /> : <div></div>}
                {this.state.moviedate ? <BookingStructure moviedates={this.state.moviedate} /> : <div></div>}

            </div >
        )
    }
}

export default Movie;
