import React, { Component } from 'react';
import axios from 'axios';

class AdminLogin extends Component {
    constructor(props) {
        super(props);

        this.state = {
            login: true,
            username: "",
            email: "",
            password: "",
            failedLogin: false,
            failedRegistration: false,
            error: "",
        }
    }

    handleChangeUsername(e) {
        this.setState({ username: e.target.value })
    }

    handleChangeEmail(e) {
        this.setState({ email: e.target.value })
    }

    handleChangePassword(e) {
        this.setState({ password: e.target.value })
    }

    handleRegisterState(e) {
        e.preventDefault()
        this.setState({ login: false })
    }

    handleLoginState(e) {
        e.preventDefault()
        this.setState({ login: true })
    }

    handleSubmitLogin(e) {
        e.preventDefault()

        axios
            .post('http://localhost:1337/auth/local', {
                identifier: this.state.username,
                password: this.state.password,
            })
            .then(res => {
                console.log("Logged in");
                this.setState({ failedLogin: false });
                this.props.userChange(res);
            })
            .catch(error => {
                console.log(error);
                if (error === undefined) {

                } else if (error.response.status === 400) {
                    var failedLoginError = "Lösenordet eller användarnamnet är inkorrekt.";
                    this.setState({ failedLogin: true, error: failedLoginError });
                }
            })
    }


    handleSubmitRegister(e) {
        e.preventDefault()

        axios
            .post('http://localhost:1337/auth/local/register', {
                username: this.state.username,
                email: this.state.email,
                password: this.state.password,
            })
            .then(res => {
                console.log("User created", res);
                this.setState({ failedRegistration: false });
            })
            .catch(error => {
                console.log(error);
                var failedRegistrationError = "Registrering misslyckades.";
                this.setState({ failedRegistration: true, error: failedRegistrationError });
            })
    }

    render() {
        return (
            <div>
                {this.state.login &&
                    <div>
                        <form onSubmit={this.handleSubmitLogin.bind(this)}>
                            <div>
                                Logga in:
                            </div>
                            {this.state.failedLogin && <div>
                                {this.state.error}
                            </div>
                            }
                            <div>
                                Användarnamn:
                            </div>
                            <div>
                                <input id={"username"} type={"text"} placeholder={"username"} onChange={this.handleChangeUsername.bind(this)}></input>
                            </div>
                            <div>
                                Lösenord:
                            </div>
                            <div>
                                <input id={"password"} type={"password"} placeholder={"password"} onChange={this.handleChangePassword.bind(this)}></input>
                            </div>
                            <div>
                                <button id={"login"} type={"submit"} onClick={this.handleSubmitLogin.bind(this)}>Logga in</button>
                            </div>
                        </form>
                        <div>
                            Har du inget ett konto?
                            <button id={"changeregister"} type={"submit"} name="changeregister" onClick={this.handleRegisterState.bind(this)}>Registera dig</button>
                        </div>
                    </div>
                }
                {!this.state.login &&
                    <div>
                        <form onSubmit={this.handleSubmitRegister.bind(this)}>
                            <div>
                                Registrera dig:
                        </div>
                            {this.state.failedRegistration && <div>
                                {this.state.error}
                            </div>
                            }
                            <div>
                                Användarnamn:
                        </div>
                            <div>
                                <input id={"username"} type={"text"} placeholder={"username"} onChange={this.handleChangeUsername.bind(this)}></input>
                            </div>
                            <div>
                                Email:
                        </div>
                            <div>
                                <input id={"email"} type={"email"} placeholder={"username"} onChange={this.handleChangeEmail.bind(this)}></input>
                            </div>
                            <div>
                                Lösenord:
                        </div>
                            <div>
                                <input id={"password"} type={"password"} placeholder={"password"} onChange={this.handleChangePassword.bind(this)}></input>
                            </div>
                            <div>
                                <button id={"register"} type={"submit"} onClick={this.handleSubmitRegister.bind(this)}>Registera</button>
                            </div>
                        </form>
                        <div>
                            Har du redan ett konto?
                        <button id={"changelogin"} type={"submit"} onClick={this.handleLoginState.bind(this)}>Logga in istället</button>
                        </div>
                    </div>
                }

            </div>
        )
    }
}

export default AdminLogin;
