import React, { Component } from "react";
import Card from "./Card";
import faker from "faker";


class Body extends Component {
    constructor(props) {
        super(props);

        this.state = {
            1: { id: 1, img: faker.random.image(), title: "Movie 1", blurb: "Some information about the movie here", onebooked: false, },
            2: { id: 2, img: faker.random.image(), title: "Movie 2", blurb: "Some information about the movie here", twobooked: false },
        }
    }

    updateMovie = (e) => {
        var id = e.target.id;
        if (id == 1) {
            if (this.state.id.onebooked == true) {
                this.setState({ id: { onebooked: false } })
            } else {
                this.setState({ id: { onebooked: true } })
            }
        } else if (id == 2) {
            if (this.state.id.twobooked == true) {
                this.setState({ id: { twobooked: false } })
            } else {
                this.setState({ id: { twobooked: true } })
            }
        }
    }

    render() {
        return (
            <div>
                {
                    this.state.map((movie) => {
                        <Card theId={this.id} image={this.image} title={this.title} blurb={this.blurb} updateMethod={this.updateMovie} />
                    })
                }
            </div>
        )
    }
}

export default Body;
