import React, { Component } from "react";
import faker from "faker";
import "../styles/_main.scss"
import Nav from "./Nav";
import Body from "./Body";


class App extends Component {

      state = { Movies: [{ id: 1, img: faker.random.image(), title: "Movie 1", blurb: "Some information about the movie here" }, { id: 2, img: faker.random.image(), title: "Movie 2", blurb: "Some information about the movie here" }], }

      updateMovies(id, newTitle, newBlurb) {
            if (newTitle != "") {
                  this.setState(Movies[id].title = newTitle)
            }
            if (newBlurb != "") {
                  this.setState(Movies[id].blurb = newBlurb)
            }
      }

      render() {
            return (
                  <main>
                        <Nav logo={faker.image.cats()} />
                        <Body data={Movies} updateMethod={this.updateMovies} />
                  </main>
            )
      }
}

