import React, { Component } from "react";
import axios from "axios";
import { Redirect, Link } from "react-router-dom";

class Shedules extends Component {
    constructor(props) {
        super(props);

        this.state = {
        }
    }

    componentDidMount() {
        axios.get('http://localhost:1337/dates/').then((res) => {
            this.setState({ dates: res.data });
        })
    }

    async onSubmitHandle(e) {
        e.preventDefault()
        var theDate = await axios.get('http://localhost:1337/dates/?id=', e.target.date.value);
        localStorage.setItem('changeShedule', JSON.stringify(theDate.data))
        this.props.history.push("/mod/editShedule")
    }

    render() {

        if (this.props.aUserGroup != "admin") {
            return (<Redirect to="/" />)
        }
        return (
            <div className={"shedulesMain"}>
                <div className={"shedulesWrapper"}>
                    <div>
                        <div>
                            Scheman
                        </div>
                        <div>
                            <Link to="/mod/createShedule">
                                <button>Lägg till schema</button>
                            </Link>
                        </div>
                    </div>
                    <table>
                        <thead>
                            <tr>
                                <th>
                                    Datum
                                </th>
                                <th>
                                    Tid
                                </th>
                                <th>
                                    Salong
                                </th>
                                <th>
                                    Film
                                </th>
                                <th>
                                    Biljetter kvar
                                </th>
                                <th>
                                    Ändra
                                </th>
                            </tr>
                        </thead>
                        <tbody>
                            {this.state.dates && this.state.dates.map((date) =>
                                <tr>
                                    <td>
                                        {console.log(date)}
                                        {date.date}
                                    </td>
                                    <td>
                                        {date.time}
                                    </td>
                                    <td>
                                        {date.salon.name}
                                    </td>
                                    <td>
                                        {date.movie.title}
                                    </td>
                                    <td>
                                        {date.TicketsLeft}
                                    </td>
                                    <td>
                                        <form className={"addForm"} onSubmit={this.onSubmitHandle.bind(this)}>
                                            <button type="submit" name="date" value={date.id} className={"addFormButton"}>Edit</button>
                                        </form>
                                    </td>
                                </tr>
                            )}
                        </tbody>
                    </table>
                </div>
            </div>
        )
    }
}

export default Shedules;
