import axios from "axios";

class DateTest extends Component {
    constructor(props) {
        super(props);

        this.state = {
        }
    }

    calculateWeek = (e) => {
        e.preventDefault();
        var thisYear = new Date();
        thisYear = thisYear.getFullYear();
        console.log(thisYear);
        var thisMonth = new Date();
        thisMonth = thisMonth.getMonth();
        console.log(thisMonth);
        var thisDay = new Date();
        thisDay = thisDay.getDay();
        console.log(thisDay);
    }


    addDate = (e) => {
        e.preventDefault();
        var theMonday = new Date();
        var test = (theMonday.getMonth() + 1) < 9
        var test2 = ""
        var month = "";
        if (test) { month = ("0" + (theMonday.getMonth() + 1)) } else { month = (theMonday.getMonth() + 1) }
        var theMonday = new Date();
        var amonday = theMonday.getFullYear() + '-' + month + '-' + (theMonday.getDate() + 1);
        console.log(amonday);
        var test1 = "";
        var test2 = "";
        var test3 = "";
        switch (theMonday.getFullYear()) {
            case 2020:
                test1 = "2020-";
                break;
            default:
                test1 = "oh dear";
        }
        switch (theMonday.getMonth()) {
            case 4:
                test2 = "05-";
                break;
            case 5:
                test2 = "05-";
                break;
            default:
                test2 = "oh dear";
        }
        switch (theMonday.getDate() + 1) {
            case 14:
                test3 = "15";
                break;
            case 15:
                test3 = "15";
                break;
            case 16:
                test3 = "16";
                break;
            default:
                test3 = "oh dear";
        }
        console.log(test1 + test2 + test3)
        //console.log(monday)
        console.log("2020-05-25")

        /*axios.post('http://localhost:1337/dates/', {
            date: "",
            time: "17:00:00.000",
            TicketsLeft: 25,
        }).then((rest) => { console.log(rest) }) */
    }


    render() {
        return (
            <div>
                <form onSubmit={this.addDate}>
                    <button type="submit" className={"addFormButton"}>Save</button>
                </form>
                <form onSubmit={this.calculateWeek}>
                    <button type="submit" className={"addFormButton"}>get</button>
                </form>
            </div>
        )
    }
}

