import React, { Component } from 'react';
import UserLogin from "./UserLogin";
import UserProfile from "./UserProfile";

class CustomerOverview extends Component {
    constructor(props) {
        super(props);

        this.state = {
            user: null || localStorage.getItem('u'),
            displayName: "",
        }
    }

    render() {

        const loggedIn = this.state.user || localStorage.getItem('u');
        return (
            <div>
                {
                    loggedIn ? <UserLogin userCredentrial={
                        (user) => {
                            this.setState({ user: user.email });
                            localStorage.setItem('user', this.state.user);
                        }
                    } showDisplayName={(username) => {
                        console.log("display name from parent: " + username);
                        //firebase.auth().onAuthStateChanged()
                    }} /> : <UserProfile userData={this.state.user} />
                }
            </div>
        )
    }
}

export default CustomerOverview;
