import React, { Component } from "react";
import Card from "./Card";
import faker from "faker";


class Main extends Component {
    constructor(props) {
        super(props);

        this.state = {
            Movies: [{ key: 0, img: faker.random.image(), title: "Movie 1", blurb: "Some information about the movie here", booked: false, }, { key: 1, img: faker.random.image(), title: "Movie 2", blurb: "Some information about the movie here", booked: false },]
        }
    }

    /* updateMovie = (Movie, theId) => {
        if (Movie.id == theId) {
            Movie.booked = !Movie.booked;
        }
    } */

    handleSubmit = (e) => {
        e.preventDefault()
        var theId = e.target.id;
        this.setState(this.state.Movies.map((Movie, theId) => {
            console.log(Movie);
            if (Movie.key == theId) {
                console.log(Movie.key);
                if (Movie.booked == true) {
                    return (Movie.booked = false);
                } else {
                    return (Movie.booked = true);
                }
            }
        }))
    }

    render() {
        var Content = this.state.Movies.map(
            (Movie) => {
                return (
                    <Card key={Movie.key} theId={Movie.key} image={Movie.img} title={Movie.title} blurb={Movie.blurb} submitMethod={this.handleSubmit} />
                )
            }
        );

        return (
            <main>
                {Content}
            </main>
        );
    }
}

export default Main;
