import React, { Component } from 'react';
import AdminLogin from '../auth/AdminLogin';
import AdminProfile from '../auth/AdminProfile';

class AdminView extends Component {
    constructor(props) {
        super(props);

        this.state = {
            user: false,
        }
    }

    changeStateUser(jwt) {
        if (jwt) {
            this.setState({ user: jwt });
        }
    }

    render() {
        return (
            <div>
                {!this.state.user && <AdminLogin userChange={(e) => { if (jwt) { this.setState({ user: jtwt }) } }} user={this.state.user} />}
                {this.state.user && <AdminProfile />}

            </div>
        )
    }
}

export default AdminView;
