import React, { Component } from "react";
import axios from "axios";

class Movie extends Component {
    constructor(props) {
        super(props);

        this.state = {
            movie: undefined,
            moviedate: undefined,
        }
    }

    componentDidMount() {
        axios
            .get('http://localhost:1337/movies/' + this.props.match.params.id)
            .then((res) => {
                this.setState({ movie: res.data });
                console.log(this.state.movie);
                this.state.movie.moviedates.map((moviedate) => {
                    axios
                        .get('http://localhost:1337/moviedates/' + moviedate.id)
                        .then((res) => {
                            var movieDate = this.state.moviedate;
                            movieDate.append(res.data)
                            this.setState({ moviedate: movieDate });
                            console.log(this.state.moviedate);
                        })
                })
            })
    }


    handleBooking = (e) => {
        e.preventDefault();
        // get local storage
        var cart = [];
        var item = undefined;
        if (localStorage.getItem('cart')) {
            //add to cart
            cart = localStorage.getItem('cart');
            item = { 'Movie': e.target.movie.value, 'Date': e.target.date.value, 'Time': e.target.time.value, 'Salon': e.target.salon.value, 'Number': e.target.number.value }
            cart.append(item);
        } else {
            //construct cart
            item = { 'Movie': e.target.movie.value, 'Date': e.target.date.value, 'Time': e.target.time.value, 'Salon': e.target.salon.value, 'Number': e.target.number.value }
            cart.append(item);
        }
        // set local storage
        localStorage.setItem('cart', cart)
    }

    render() {
        return (
            <div> Test { console.log(this.state)} {console.log(this.props.match.params.id)}</div >
        )
    }
}

export default Movie;
