import React, { Component } from "react";
import axios from "axios";
import { Redirect, withRouter } from "react-router-dom";

class HandleShedule extends Component {
    constructor(props) {
        super(props);

        this.state = {
        }
    }
    async componentDidMount() {
        const res1 = await axios.get('http://localhost:1337/movies/')
        const res2 = await axios.get('http://localhost:1337/salons/')
        const res3 = await axios.get('http://localhost:1337/dates/')
        this.setState({ movies: res1.data, salons: res2.data, dates: res3.data })
    }

    handleSalon(e) {
        this.setState({ salon: e.value })
    }

    async onSubmitHandleSaveWeek(e) {

        // get the last date so we can easily add a week
        axios.get('http://localhost:1337/dates/?salon.id=', this.state.salon).then((res) => {
            var allDates = res.data;
            var lastDate = res.data[res.data.length - 1];
            this.setState({ latestDate: lastDate });
        })
        if (!this.state.latestDate) {
            this.setState({ latestDate: null });
        }

        //calculate the dates of next week
        var monday = undefined;
        var tuesday = undefined;
        var wednesday = undefined;
        var thursday = undefined;
        var friday = undefined;
        var saturday = undefined;
        var sunday = undefined;
        if (this.state.latestDate) {
            // latestDate should always be a sunday
            monday = this.state.latestDate.add(1).day()
            tuesday = this.state.latestDate.add(2).day();
            wednesday = this.state.latestDate.add(3).day();
            thursday = this.state.latestDate.add(4).day();
            friday = this.state.latestDate.add(5).day();
            saturday = this.state.latestDate.add(6).day();
            sunday = this.state.latestDate.add(7).day();
        } else {
            var today = Date.today()
            switch (today.getDay()) {
                case 0:
                    monday = today.add(1).day();
                    tuesday = today.add(2).day();
                    wednesday = today.add(3).day();
                    thursday = today.add(4).day();
                    friday = today.add(5).day();
                    saturday = today.add(6).day();
                    sunday = today.add(7).day();
                    break;
                case 1:
                    monday = today;
                    tuesday = today.add(1).day();
                    wednesday = today.add(2).day();
                    thursday = today.add(3).day();
                    friday = today.add(4).day();
                    saturday = today.add(5).day();
                    sunday = today.add(6).day();
                    break;
                case 2:
                    monday = today.add(6).day();
                    tuesday = today.add(7).day();
                    wednesday = today.add(8).day();
                    thursday = today.add(9).day();
                    friday = today.add(10).day();
                    saturday = today.add(11).day();
                    sunday = today.add(12).day();
                    break;
                case 3:
                    monday = today.add(5).day();
                    tuesday = today.add(6).day();
                    wednesday = today.add(7).day();
                    thursday = today.add(8).day();
                    friday = today.add(9).day();
                    saturday = today.add(10).day();
                    sunday = today.add(11).day();
                    break;
                case 4:
                    monday = today.add(4).day();
                    tuesday = today.add(5).day();
                    wednesday = today.add(5).day();
                    thursday = today.add(6).day();
