import React, { Component } from "react";
import axios from "axios";
import MovieInfo from './MovieInfo';
import BookingStructure from './BookingStructure';

class Movie extends Component {
    constructor(props) {
        super(props);
        this.state = {
            movie: undefined,
            moviedates: undefined,
        }
    }

    componentDidMount() {
        var presentation = new Array();
        console.log(typeof (presentation));
        var numberOfTickts = undefined;
        axios
            .get('http://localhost:1337/movies/',
                {
                    params: {
                        id: this.props.match.params.id
                    }
                })
            .then((res) => {
                axios.get('http://localhost:1337/tickets/')
                    .then((test) => {
                        if (test.data.length == 0) {
                            numberOfTickts = 0;
                            this.setState({ movie: res.data[0] });
                            axios
                                .get('http://localhost:1337/dates/')
                                .then((result) => {
                                    this.state.movie.moviedates.map((moviedate) => {
                                        var i = 0;
                                        console.log(typeof (presentation))
                                        while (i < result.data.length) {
                                            if (result.data[i].id == moviedate.date) {
                                                console.log(result.data[i]);
                                                var ticketsLeft = result.data[i].salons[0].seats - numberOfTickts;
                                                var item = { 'Movietitle': res.data[0].title, 'MovieID': res.data[0].id, 'Date': result.data[i].date, 'DateID': result.data[i].id, 'Salon': result.data[i].salons[0].salon, 'SalonID': result.data[i].salons[0].id, 'TicketsLeft': ticketsLeft }
                                                presentation.push(item)
                                                console.log(typeof (presentation))
                                            }
                                            i = i + 1;
                                        }
                                    });
                                    console.log(typeof (presentation))
                                    this.setState({ moviedates: presentation });
                                })
                        } else {
                            console.log(test)
                        }
                    })
            })
    }

    handleBooking = (e) => {
        e.preventDefault();
        // get local storage
        var cart = [];
        var item = undefined;
        if (localStorage.getItem('cart')) {
            //add to cart
            cart = localStorage.getItem('cart');
            item = { 'Movie': e.target.movie.value, 'Date': e.target.date.value, 'Time': e.target.time.value, 'Salon': e.target.salon.value, 'Number': e.target.number.value }
            cart.append(item);
        } else {
            //construct cart
            item = { 'Movie': e.target.movie.value, 'Date': e.target.date.value, 'Time': e.target.time.value, 'Salon': e.target.salon.value, 'Number': e.target.number.value }
            cart.append(item);
        }
        // set local storage
        localStorage.setItem('cart', cart)
    }

    render() {
        return (
            <div>
                {this.state.movie ? <MovieInfo movie={this.state.movie} /> : <div></div>}
                <div className={"card"} style={{ width: "18rem" }}>
                    <div className={"card-wrapper"}>
                        <table>
                            {this.state.moviedates ? < BookingStructure moviedates={this.state.moviedates} /> : <tbody><tr><td></td></tr></tbody>}
                        </table>
                    </div>
                </div>
            </div>
        )
    }
}

export default Movie;
