import React, { Component } from "react";
import Card from "./Card";
import faker from "faker";


class Body extends Component {
    constructor(props) {
        super(props);

        this.state = {
            1: { id: 1, img: faker.random.image(), title: "Movie 1", blurb: "Some information about the movie here" },
            onebooked: false,
            2: { id: 2, img: faker.random.image(), title: "Movie 2", blurb: "Some information about the movie here" },
            twobooked: false
        }
    }

    updateMovie = (e) => {
        var id = e.target.id;
        if (id == 1) {
            if (this.state.onebooked == true) {
                this.setState({ onebooked: false })
            } else {
                this.setState({ onebooked: true })
            }
        } else if (id == 2) {
            this.setState({ twobooked: !twobooked })
        }
    }

    render() {
        return (
            <div>
                {
                    this.state.map((id, image, title, blurb) => {
                        <Card theId={id} image={image} title={title} blurb={blurb} updateMethod={this.updateMovie} />
                    })
                }
            </div>
        )
    }
}

export default Body;
