import React, { Component } from "react";
import axios from "axios";
import MovieInfo from './MovieInfo';
import BookingStructure from './BookingStructure';

class Movie extends Component {
    constructor(props) {
        super(props);
        this.state = {
            movie: undefined,
        }
    }

    componentDidMount() {
        var present = [];
        var numberOfTickts = undefined;
        axios
            .get('http://localhost:1337/movies/',
                {
                    params: {
                        id: this.props.match.params.id
                    }
                })
            .then((res) => {
                axios.get('http://localhost:1337/tickets/')
                    .then((test) => {
                        if (test.data.length == 0) {
                            numberOfTickts = 0;
                            this.setState({ movie: res.data[0] });
                            axios
                                .get('http://localhost:1337/dates/')
                                .then((result) => {
                                    this.state.movie.moviedates.map((moviedate) => {
                                        var i = 0;
                                        while (i < result.data.length) {
                                            if (result.data[i].id == moviedate.date) {
                                                var ticketsLeft = result.data[i].salons[0].seats - numberOfTickts;
                                                var item = { 'Movietitle': res.data[0].title, 'MovieID': res.data[0].id, 'Date': result.data[i].date, 'DateID': result.data[i].id, 'Salon': result.data[i].salons[0].salon, 'SalonID': result.data[i].salons[0].id, 'TicketsLeft': ticketsLeft, 'MoviedateID': moviedate.id }
                                                present.push(item);
                                            }
                                            i = i + 1;
                                        }
                                    })
                                })

                            this.setState({ moviedates: present });
                        } else {
                            console.log(test)
                        }
                    })
            })
    }



    handleBooking = (e) => {
        e.preventDefault();
        // get local storage
        var cart = [];
        var item = undefined;
        if (localStorage.getItem('cart')) {
            //add to cart
            cart = localStorage.getItem('cart');
            item = { 'Movie': e.target.movie.value, 'Date': e.target.date.value, 'Time': e.target.time.value, 'Salon': e.target.salon.value, 'Number': e.target.number.value }
            cart.append(item);
        } else {
            //construct cart
            item = { 'Movie': e.target.movie.value, 'Date': e.target.date.value, 'Time': e.target.time.value, 'Salon': e.target.salon.value, 'Number': e.target.number.value }
            cart.append(item);
        }
        // set local storage
        localStorage.setItem('cart', cart)
    }


    render() {
        console.log(this.state.moviedates)
        if (this.state.moviedates) {
            console.log("here")
            console.log(typeof this.state.moviedates)
            this.state.moviedates.map((md, i) => {
                console.log("here2")
                console.log(md)
                console.log(i)
            })
            console.log("here3")
        }
        return (
            <div>
                {this.state.movie ? <MovieInfo movie={this.state.movie} /> : <div></div>}
                <div className={"card"} style={{ width: "18rem" }}>
                    <div className={"card-wrapper"}>
                        <form className={"showForm"} onClick={this.handleBooking.bind(this)}>
                            <table>
                                <tbody><tr>
                                    <td>
                                        Apple
                                    <input type={"hidden"} name={"movie"} value={2} />
                                    Apple
                                </td>
                                    <td>
                                        <input type={"hidden"} name={"salon"} value={2} />
                                    2
                                </td>
                                    <td>
                                        <input type={"hidden"} name={"date"} value={4} />
                                    May 1 2020 17:00
                                </td>
                                    <td>
                                        25
                                </td>
                                    <td>
                                        <input type={"hidden"} name={"moviedate"} value={4} />
                                        <input type={'number'} name={"amount"} max={25} />
                                    </td><td>
                                        <button onClick={this.handleBooking.bind(this)}>
                                            Lägg i varukorg
                                </button></td></tr></tbody>
                            </table></form>
                    </div>
                </div>
            </div>
        )
    }
}

//{this.state.moviedates && < BookingStructure moviedates={this.state.moviedates} />} put this back

export default Movie;
