import React, { Component } from "react";
import axios from "axios";
import MovieInfo from './MovieInfo';
import BookingStructure from './BookingStructure';

class Movie extends Component {
    constructor(props) {
        super(props);
        this.state = {
            movie: undefined,
        }
    }

    componentDidMount() {
        var present = [];
        var numberOfTickts = undefined;
        axios
            .get('http://localhost:1337/movies/',
                {
                    params: {
                        id: this.props.match.params.id
                    }
                })
            .then((res) => {
                axios.get('http://localhost:1337/tickets/')
                    .then((test) => {
                        if (test.data.length == 0) {
                            numberOfTickts = 0;
                            this.setState({ movie: res.data[0] });
                            axios
                                .get('http://localhost:1337/dates/')
                                .then((result) => {
                                    this.state.movie.moviedates.map((moviedate) => {
                                        var i = 0;
                                        while (i < result.data.length) {
                                            if (result.data[i].id == moviedate.date) {
                                                var ticketsLeft = result.data[i].salons[0].seats - numberOfTickts;
                                                var item = { 'Movietitle': res.data[0].title, 'MovieID': res.data[0].id, 'Date': result.data[i].date, 'DateID': result.data[i].id, 'Salon': result.data[i].salons[0].salon, 'SalonID': result.data[i].salons[0].id, 'TicketsLeft': ticketsLeft, 'MoviedateID': moviedate.id }
                                                present.push(item);
                                            }
                                            i = i + 1;
                                        }
                                    })
                                })

                            this.setState({ moviedates: present });
                        } else {
                            console.log(test)
                        }
                    })
            })
    }


    render() {
        console.log(this.state.moviedates)
        if (this.state.moviedates) {
            console.log("here")
            console.log(typeof this.state.moviedates)
            this.state.moviedates.map((md, i) => {
                console.log("here2")
                console.log(md)
                console.log(i)
            })
            console.log("here3")
        }
        return (
            <div>
                {this.state.movie ? <MovieInfo movie={this.state.movie} /> : <div></div>}
                <div className={"card"} style={{ width: "18rem" }}>
                    <div className={"card-wrapper"}>
                        <table>
                            {this.state.moviedates ? < BookingStructure moviedates={this.state.moviedates} /> : <form><tbody><tr><td></td></tr></tbody></form>}
                        </table>
                    </div>
                </div>
            </div>
        )
    }
}

export default Movie;
